-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `bid` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(100) NOT NULL,
  `author` varchar(100) NOT NULL,
  `time` date NOT NULL,
  `summary` mediumtext,
  `type` varchar(50) DEFAULT NULL,
  `clicks` int(50) DEFAULT NULL,
  `subscriptions` int(50) DEFAULT NULL,
  `status` varchar(50) DEFAULT NULL,
  `totalscore` int(50) DEFAULT NULL,
  `value` int(50) DEFAULT NULL,
  `image` longblob,
  `path` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`bid`)
) ENGINE=InnoDB AUTO_INCREMENT=110 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of book
-- ----------------------------

-- ----------------------------
-- Table structure for chapter
-- ----------------------------
DROP TABLE IF EXISTS `chapter`;
CREATE TABLE `chapter` (
  `bid` int(11) NOT NULL,
  `bno` int(10) NOT NULL,
  `cname` varchar(20) DEFAULT NULL,
  `time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP,
  `chapter` mediumtext NOT NULL,
  PRIMARY KEY (`bid`,`bno`),
  CONSTRAINT `chapter_ibfk_1` FOREIGN KEY (`bid`) REFERENCES `book` (`bid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

