  PRIMARY KEY (`uid`,`bid`),
  KEY `bid` (`bid`),
  CONSTRAINT `score_ibfk_2` FOREIGN KEY (`bid`) REFERENCES `book` (`bid`),
  CONSTRAINT `score_ibfk_3` FOREIGN KEY (`uid`) REFERENCES `user` (`uid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

