DROP TRIGGER IF EXISTS `CK_BOOKIN`;
DELIMITER ;;
CREATE TRIGGER `CK_BOOKIN` BEFORE INSERT ON `book` FOR EACH ROW begin
   set NEW.clicks = 0;
   set NEW.subscriptions = 0;
   set NEW.totalscore = 0;
   set NEW.value = 0;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `CK_BOOKUP`;
DELIMITER ;;
CREATE TRIGGER `CK_BOOKUP` BEFORE UPDATE ON `book` FOR EACH ROW begin
   set new.value = new.clicks + new.subscriptions*10;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `CK_SCOREIN`;
DELIMITER ;;
CREATE TRIGGER `CK_SCOREIN` AFTER INSERT ON `score` FOR EACH ROW begin
declare sc int;
declare sclp int;
declare s int; 
set sc = (select count(*) from score group by bid having bid = NEW.bid);
set sclp = (select sum(point) from score group by bid having bid = NEW.bid);
set s = sclp/sc;

update book set totalscore = s
where bid = new.bid;
end
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `CK_SUBIN`;
DELIMITER ;;
CREATE TRIGGER `CK_SUBIN` AFTER INSERT ON `subscribe` FOR EACH ROW begin
   update book 
   set subscriptions = subscriptions +1
   where bid = NEW.bid;
end
;;
DELIMITER ;
